    ASSERT(buffer);

    size_t count = 32;

    octaspire_memory_allocator_set_number_and_type_of_future_allocations_to_be_rigged(allocator, count, 0);

    ASSERT_EQ(count, octaspire_memory_allocator_get_number_of_future_allocations_to_be_rigged(allocator));

