    ASSERT_EQ(OCTASPIRE_DERN_VALUE_TAG_BOOLEAN, evaluatedValue->typeTag);
    ASSERT_EQ(true,                             evaluatedValue->value.boolean);



    // From beginning
    evaluatedValue = octaspire_dern_vm_read_from_c_string_and_eval_in_global_environment(
        vm,
        "(port-seek f 0)");

    ASSERT_EQ(OCTASPIRE_DERN_VALUE_TAG_BOOLEAN, evaluatedValue->typeTag);
