    {
        abort();
    }

    if (!octaspire_dern_vm_pop_value(self, self->valueTrue->docstr))
    {
        abort();
    }

    if (!octaspire_dern_vm_pop_value(self, self->valueTrue))
    {
        abort();
    }

    self->valueFalse = octaspire_dern_vm_create_new_value_boolean(self, false);

